mapper.select:

		select distinct cateCode from kkb_bean where 






		  							authorId = #{authorId}





  						beanName like concat ('%', #{keyword}, '%')


		select DISTINCT count(*) cateCount, a.cateCode,b.cateName from kkb_bean a left join kkb_cate b on a.cateCode = b.cateCode 

		where 











		  							authorId = #{authorId}







  						and a.cateCode = #{cateCode}





  						beanName like concat ('%', #{keyword}, '%') and a.cateCode = #{cateCode}
